import { Data, animate, Override, Animatable } from 'framer'
const data = Data({
  //   scale: Animatable(1),
  scaleDatas: [Animatable(1), Animatable(1), Animatable(1), Animatable(1)],
})

window.log = console.log

export const Scale: Override = props => {
  let currentIndex = props.children[0].props.children[0].props.currentIndex

  log('currentIndex', currentIndex)

  return {
    scale: data.scaleDatas[currentIndex],
    onTap() {
      data.scaleDatas[currentIndex].set(0.6)
      animate.spring(data.scaleDatas[currentIndex], 1)
    },
  }
}
